const escapeRegExp = (string) => {
  return string.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'); // $& means the whole matched string
}

const regexQuery = {
  $regex: `.*${escapeRegExp({{search_bar.value}})}.*`,
  $options:'i'
}

return {
  $or: [
    { _id: regexQuery },
    { firstName: regexQuery },
    { lastName: regexQuery },
    { email: regexQuery },
  ],
}

